/*----------------Johnny-Five---------------*/
 var five = require('johnny-five');
 var board = new five.Board();

 /*----------------PubNub Authentication---------------*/
 var PubNub = require('pubnub');
 var pubnub = new PubNub({
 subscribe_key : 'sub-c-f3ab7140-7a1e-11e7-8cce-0619f8945a4f',
 publish_key : 'pub-c-bbf07648-4161-4d99-8fe6-f1687915450e',
 ssl : true
 });

 var channel = 'led';


 /*----------------Board---------------*/
 board.on('ready', function(){

 /*----------------Seven Segment Led Display---------------*/
 var led = new five.Led(13);
 var led12 = new five.Led(12);
 var led11 = new five.Led(11);
 var led10 = new five.Led(10);
 var led9 = new five.Led(9);
 var led8 = new five.Led(8);
 var led7 = new five.Led(7);
 var led6 = new five.Led(6);
 var led5 = new five.Led(5);


 /*----------------Exercise Leds---------------*/
 var led46 = new five.Led(46);
 var led47 = new five.Led(47);
 var led48 = new five.Led(48);
 var led49 = new five.Led(49);
 var led50 = new five.Led(50);
 var led51 = new five.Led(51);
 var led52 = new five.Led(52);
 var led53 = new five.Led(53);
 var leds = new five.Leds([46, 47, 48, 49, 50, 51, 52, 53]);

 /*--------------------Servo Motor------------------*/
 var servo = new five.Servo({
 id: "MyServo", // User defined id
 pin: 4, // Which pin is it attached to?
 type: "continuous", // Default: "standard". Use "continuous" for continuous rotation servos
 fps: 100, // Used to calculate rate of movement between positions
 range: [0, 180], // Default: 0-180
 startAt: 90, // Immediately move to a degree
 center: true // overrides startAt if true and moves the servo to the center of the range
 });


 var joystick = new five.Joystick({
 pins: ["A0", "A1"]
 });

 board.repl.inject({
 servo: servo
 });

 servo.center();

 /*-----------------Joystick------------------*/
 joystick.on("change", function() {
 if(this.y > 0.5){
 servo.max();
 }else servo.center();
 if(-this.y > 0.5){
 servo.min();
 }else servo.center();
 });

 /*----------------PubNub Subscribe---------------*/
 pubnub.subscribe({
 channel: channel,

 /*----------------YOUR CODE HERE---------------*/

 message: function(m){
 var count = 0;
 if(m.blink === true){
 for (var i=0 in m.array.length){
 if(m.array[i] == 0){
 leds[i].off();
 } else {
 leds[i].blink(500);
 count++;
 }
 }
 display(count);
 servo.to(m.servo);
 } else {
 for (var i=0 in m.array.length){
 leds[i].stop();
 }
 display(0);
 servo.center();
 }
 },
 error: function(err) {console.log(err);}
 });


 /*----------------Auxiliary Methods---------------*/
 function turnOffAll(){
 led12.off();
 led11.off();
 led10.off();
 led9.off();
 led8.off();
 led7.off();
 led6.off();
 led5.off();
 led46.off();
 led48.off();
 led47.off();
 led49.off();
 led50.off();
 led51.off();
 led52.off();
 led53.off();
 }
 function display(num){
 turnOffAll();
 switch(num){
 case 0:
 led12.on();
 led11.on();
 led9.on();
 led8.on();
 led7.on();
 led6.on();
 break;

 case 1:
 led11.on();
 led9.on();
 break;

 case 2:
 led12.on();
 led11.on();
 led8.on();
 led7.on();
 led5.on();
 break;

 case 3:
 led12.on();
 led11.on();
 led8.on();
 led9.on();
 led5.on();
 break;

 case 4:
 led6.on();
 led11.on();
 led5.on();
 led9.on();
 break;

 case 5:
 led12.on();
 led6.on();
 led8.on();
 led9.on();
 led5.on();
 break;

 case 6:
 led7.on();
 led6.on();
 led8.on();
 led9.on();
 led5.on();
 led12.on();
 break;

 case 7:
 led12.on();
 led11.on();
 led9.on();
 break;

 case 8:
 led12.on();
 led11.on();
 led8.on();
 led9.on();
 led5.on();
 led6.on();
 led7.on();
 break;

 case 9:
 led12.on();
 led11.on();
 led8.on();
 led9.on();
 led5.on();
 led6.on();
 break;

 default:
 turnOffAll();
 }
 }
 });
